mapper.select:
SELECT r.id, r.title, r.cover, r.synopsis, r.status, r.good_count, r.read_count, r.release_time, r.author_id, u.account AS authorAccount, u.username AS authorUsername, u.avatar AS authorAvatar, u.synopsis AS authorSynopsis FROM recipe r LEFT JOIN user u ON r.author_id = u.id
AND r.is_lock=${@com.liezh.domain.constant.GlobalConstants@NO_LOCK}
AND r.title LIKE CONCAT('%',#{title},'%')
AND r.author_id = #{author_id}
AND r.status = #{status}
ORDER BY r.create_time DESC

SELECT r.id, r.title, r.cover, r.synopsis, r.status, r.good_count, r.read_count, r.release_time,
              r.author_id,
              u.account AS authorAccount,     u.username AS authorUsername,
              u.avatar AS authorAvatar,      u.synopsis AS authorSynopsis
FROM recipe r LEFT JOIN user u ON r.author_id = u.id
WHERE
r.id=#{recipeId}
AND r.is_lock=${@com.liezh.domain.constant.GlobalConstants@NO_LOCK}
AND u.is_lock=${@com.liezh.domain.constant.GlobalConstants@NO_LOCK}

mapper.update:
UPDATE recipe
title = #{title}, cover = #{cover}, content = #{content}, synopsis = #{synopsis}, process = #{process}, materials = #{materials}, status = #{status}, author_id = #{authorId}, good_count = #{goodCount}, read_count = #{readCount}, read_count = #{readCount},
update_time=now(), is_lock=#{isLock}
WHERE id=#{id}

mapper.select:
SELECT count(1)
FROM recipe
WHERE id = #{recipeId}
