mapper.update:
UPDATE tag

    name=#{name}, 
    query_count=#{queryCount}, 

    update_time=now(),
    is_lock=#{isLock} 

WHERE id=#{id}

mapper.select:
SELECT id, name, query_count, create_time, update_time
FROM tag
WHERE
is_lock = ${@com.liezh.domain.constant.GlobalConstants@NO_LOCK}
AND name = #{name}
